para.programlisting:
## ## samba.server ##
if [ ! -d /usr/bin ] then # /usr がマウントされていない exit fi
killproc() { # 名前指定でプロセスを停止 pid=`/usr/bin/ps -e | /usr/bin/grep -w $1 | /usr/bin/sed -e 's/^ *//' -e 's/ .*//'` [ "$pid" != "" ] && kill $pid }
# Sambaサーバーに必要な起動/停止プロセス
case "$1" in
'start') # # 使用環境(パス、ワークグループ、ホスト)に合わせて以下を編集すること # echo Starting SMBD /usr/local/samba/bin/smbd -D -s \ /usr/local/samba/smb.conf
echo Starting NMBD /usr/local/samba/bin/nmbd -D -l \ /usr/local/samba/var/log -s /usr/local/samba/smb.conf
echo Starting Winbind Daemon /usr/local/samba/sbin/winbindd ;;
'stop') killproc nmbd killproc smbd killproc winbindd ;;
*) echo "Usage: /etc/init.d/samba.server { start | stop }" ;; esac
